private boolean isNotRelevantSituation(DetailAST ast, int currentType) {
    final int parentType = ast.getParent().getType();
    final boolean starImport = currentType == TokenTypes.STAR && parentType == TokenTypes.DOT;
    final boolean slistInsideCaseGroup = currentType == TokenTypes.SLIST && parentType == TokenTypes.CASE_GROUP;
    final boolean starImportOrSlistInsideCaseGroup = starImport || slistInsideCaseGroup;
    final boolean colonOfCaseOrDefaultOrForEach = isColonOfCaseOrDefault(currentType, parentType) || isColonOfForEach(currentType, parentType);
    final boolean emptyBlockOrType = isEmptyBlock(ast, parentType) || allowEmptyTypes && isEmptyType(ast);
    return starImportOrSlistInsideCaseGroup || colonOfCaseOrDefaultOrForEach || emptyBlockOrType || isArrayInitialization(currentType, parentType);
}

