@Override
public void write(final String str) throws IOException {
    try {
        int len = 0;
        if (str != null) {
            len = str.length();
        }
        beforeWrite(len);
        out.write(str);
        afterWrite(len);
    } catch (final IOException e) {
        handleIOException(e);
    }
}

