@Override
public Allocation allocate(ApplicationAttemptId applicationAttemptId, List<ResourceRequest> ask, List<SchedulingRequest> schedulingRequests, List<ContainerId> release, List<String> blacklistAdditions, List<String> blacklistRemovals, ContainerUpdates updateRequests) {
    FifoAppAttempt application = getApplicationAttempt(applicationAttemptId);
    if (application == null) {
        LOG.error("Calling allocate on removed or non existent application " + applicationAttemptId.getApplicationId());
        return EMPTY_ALLOCATION;
    }
    if (!application.getApplicationAttemptId().equals(applicationAttemptId)) {
        LOG.error("Calling allocate on previous or removed " + "or non existent application attempt " + applicationAttemptId);
        return EMPTY_ALLOCATION;
    }
    normalizeResourceRequests(ask);
    releaseContainers(release, application);
    synchronized (application) {
        if (application.isStopped()) {
            LOG.info("Calling allocate on a stopped " + "application " + applicationAttemptId);
            return EMPTY_ALLOCATION;
        }
        if (!ask.isEmpty()) {
            LOG.debug("allocate: pre-update" + " applicationId=" + applicationAttemptId + " application=" + application);
            application.showRequests();
            application.updateResourceRequests(ask);
            LOG.debug("allocate: post-update" + " applicationId=" + applicationAttemptId + " application=" + application);
            application.showRequests();
            LOG.debug("allocate:" + " applicationId=" + applicationAttemptId + " #ask=" + ask.size());
        }
        application.updateBlacklist(blacklistAdditions, blacklistRemovals);
        Resource headroom = application.getHeadroom();
        application.setApplicationHeadroomForMetrics(headroom);
        return new Allocation(application.pullNewlyAllocatedContainers(), headroom, null, null, null, application.pullUpdatedNMTokens());
    }
}

