            application.showRequests();
            LOG.debug("allocate:" + " applicationId=" + applicationAttemptId + " #ask=" + ask.size());
        }
        application.updateBlacklist(blacklistAdditions, blacklistRemovals);
        ContainersAndNMTokensAllocation allocation = application.pullNewlyAllocatedContainersAndNMTokens();
        return new Allocation(allocation.getContainerList(), application.getHeadroom(), null, null, null, allocation.getNMTokenList());
    }
}

