    itr.seek(bytes(LOG_COMMITTED_TXN));
    Configuration config = new Configuration(false);
    itr.next();
    while (itr.hasNext()) {
        Map.Entry<byte[], byte[]> entry = itr.next();
        config.set(new String(entry.getKey(), StandardCharsets.UTF_8), new String(entry.getValue(), StandardCharsets.UTF_8));
    }
    return config;
}
